import './Images.css';
import React, { Component } from 'react';
import Popup from './Popup'

export class Images extends Component {
    constructor(props){
        super(props)
        this.state={
            ImageUrl:'',
            ImageUrlArray:[
                "portfolio/my-react-gallery/public/images/camainpage.jpg","portfolio/my-react-gallery/public/images/includehomepage.jpg","portfolio/my-react-gallery/public/images/includemenu.jpg","portfolio/my-react-gallery/public/images/includeprofile.jpg",
                "portfolio/my-react-gallery/public/images/landingca.jpg","portfolio/my-react-gallery/public/images/landinginclude.jpg", "portfolio/my-react-gallery/public/images/playgroundinclude.jpg","portfolio/my-react-gallery/public/images/programca.jpg",
                "portfolio/my-react-gallery/public/images/programinclude.jpg","portfolio/my-react-gallery/public/images/setprofileca.jpg","portfolio/my-react-gallery/public/images/signinca.jpg","portfolio/my-react-gallery/public/images/signupca.jpg"
            ],
            showModal:false,
            popImageUrl:''
        }
    }

    imageSubmitter=(e)=>{
        e.preventDefault();
        let ImageUrlArray = this.state.ImageUrlArray;
        ImageUrlArray.push(this.state.ImageUrl)
        this.setState({
            ImageUrlArray:ImageUrlArray
        })
    }

    handleLinkChange =(e)=>{
        e.preventDefault();
        this.setState({
            ImageUrl: e.target.value
        })
    }

    handlePopup =(url)=>{
        this.setState({
            showModal: this.state.showModal,
            popImageUrl: url
        })
    }

    render() {
        let ImageUrlArray = this.state.ImageUrlArray;
        const images = ImageUrlArray.map((url,Index)=>{
            return (
                <img className="singleImage" src={url} key={Index} onClick={()=>this.handlePopup(url)} alt=""/>
            )
        })
        return (
            <div>
                This is how it looks
                <form onChange={this.imageSubmitter}>
                    <input/>
                    <button></button>
                </form>
                <img className="singleImage" src="images/landingca.jpg" alt=""></img>
                <img className="singleImage" src="images/camainpage.jpg" alt=""></img>
                <img className="singleImage" src="images/includehomepage.jpg" alt=""></img>
                <img className="singleImage" src="images/landinginclude.jpg" alt=""></img>
                <img className="singleImage" src="images/setprofileca.jpg" alt=""></img>
                <img className="singleImage" src="images/includemenu.jpg" alt=""></img>
                <img className="singleImage" src="images/playgroundinclude.jpg" alt=""></img>
                <img className="singleImage" src="images/includeprofile.jpg" alt=""></img>
                <img className="singleImage" src="images/signinca.jpg" alt=""></img>
                <img className="singleImage" src="images/signupca.jpg" alt=""></img>
                <img className="singleImage" src="images/programca.jpg" alt=""></img>
                <img className="singleImage" src="images/programinclude.jpg" alt=""></img>
                {/* <img className="singleImage" src={url} key={Index} alt=""/> */}
                <images/>
                { this.state.showModal = (
                    <Popup 
                    popImageUrl={this.state.popImageUrl}
                    closePopup={this.handlePopup}
                    />
                )}
            </div>
        )
    }
}

export default Images
